import React, { useState, useEffect } from 'react';
import '../styles/Home.css';

const helloTranslations = [
    { language: 'English', text: 'Hello World' },
    { language: 'Portuguese', text: 'Olá Mundo' },
    { language: 'Spanish', text: 'Hola Mundo' },
    { language: 'French', text: 'Bonjour le Monde' },
    { language: 'German', text: 'Hallo Welt' },
    { language: 'Italian', text: 'Ciao Mondo' },
    { language: 'Japanese', text: 'こんにちは世界' },
    { language: 'Chinese', text: '你好，世界' }
  ];

function Home() {
  const [currentCountryIndex, setCurrentCountryIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHelloIndex((prevIndex) => (prevIndex + 1) % countriesVisited.length);
    }, 3000); // Troca de país a cada 3 segundos

    return () => clearInterval(interval); // Limpar o intervalo quando o componente for desmontado
  }, []);

  return (
    <div 
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundImage: 'url(/images/bg-image.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        zIndex: -1,
    }}
    >
      <div className="home-container">
        <div className="image-container">
          <img src="/images/my-photo.jpg" alt="Luis Oliveira" />
        </div>
        <div className="text-container">
          <h1>Hello World</h1>
          <p>Countries I've visited: {countriesVisited[currentCountryIndex]}</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
