import React, { useState, useEffect } from 'react';
import '../styles/Home.css';

const helloTranslations = [
  { language: 'English', text: 'Hello World' },
  { language: 'Portuguese', text: 'Olá Mundo' },
  { language: 'Spanish', text: 'Hola Mundo' },
  { language: 'French', text: 'Bonjour le Monde' },
  { language: 'Italian', text: 'Ciao Mondo' },
];

function Home() {
  const [currentHelloIndex, setCurrentHelloIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHelloIndex((prevIndex) => (prevIndex + 1) % helloTranslations.length);
    }, 2000);

    return () => clearInterval(interval); // Limpar o intervalo quando o componente for desmontado
  }, []);

  return (
    <div
      style={{
        backgroundImage: 'url(/images/bg-image.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        minHeight: '100vh', // Garantir que o conteúdo ocupa pelo menos 100% da altura da tela
      }}
    >
      <div className="home-container">
        <div className="image-container">
          <img src="/images/my-photo.jpg" alt="Luis Oliveira" />
        </div>
        <div className="text-container">
          <h1>{helloTranslations[currentHelloIndex].text}</h1>
          <p className="animate-pulse text-xl text-white shadow-lg">A dev on a quest: to learn, to build, to conquer.</p>
        </div>
      </div>

      {/* Footer */}
      <div className="bg-gray-900 text-white py-6 px-4 mt-8">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between">
          <p className="text-sm text-gray-400 mb-2 md:mb-0">
            © {new Date().getFullYear()} mugiwara.dev — All rights reserved.
          </p>
          <div className="flex gap-4 text-gray-400">
            <a href="https://github.com/oliveirammluis" target="_blank" rel="noopener noreferrer" className="hover:text-white transition">
              GitHub
            </a>
            <a href="https://www.linkedin.com/in/oliveirammluis/" target="_blank" rel="noopener noreferrer" className="hover:text-white transition">
              LinkedIn
            </a>
            <a href="mailto:teu@email.com" className="hover:text-white transition">
              Email
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
