import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

function BookACall() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'Personal_Portefolio',   
        'template_7ipf30u',    // substitui pelo teu ID de template
        form.current,
        'PnQaTaTut6OZf-AzA'      // substitui pela tua public key
      )
      .then(
        (result) => {
          console.log(result.text);
          alert('Message sent successfully!');
          form.current.reset();
        },
        (error) => {
          console.log(error.text);
          alert('Something went wrong, try again.');
        }
      );
  };

  return (
    <div className="p-8 max-w-xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Book a Call</h1>
      <p className="mb-6">Fill out the form and Iâ€™ll get back to you shortly.</p>
      <form ref={form} onSubmit={sendEmail} className="flex flex-col gap-4">
        <input
          type="text"
          name="user_name"
          placeholder="Your name"
          required
          className="p-3 border border-gray-300 rounded"
        />
        <input
          type="email"
          name="user_email"
          placeholder="Your email"
          required
          className="p-3 border border-gray-300 rounded"
        />
        <textarea
          name="message"
          placeholder="Your message"
          rows="5"
          required
          className="p-3 border border-gray-300 rounded"
        ></textarea>
        <button
          type="submit"
          className="bg-black text-white py-3 px-6 rounded hover:bg-gray-800 transition"
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default BookACall;
