import React, { useState, useEffect } from 'react';
import '../styles/Home.css';

const countriesVisited = [
  "Portugal", "Spain", "England", "Czech Republic", "USA", "Japan"
];

function Home() {
  const [currentCountryIndex, setCurrentCountryIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentCountryIndex((prevIndex) => (prevIndex + 1) % countriesVisited.length);
    }, 3000); // Troca de país a cada 3 segundos

    return () => clearInterval(interval); // Limpar o intervalo quando o componente for desmontado
  }, []);

  return (
    <div 
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundImage: 'url(/images/bg-image.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        height: '100vh',
        paddingTop: '30px'
      }}
    >
      <div className="home-container">
        <div className="image-container">
          <img src="/images/my-photo.jpg" alt="Luis Oliveira" />
        </div>
        <div className="text-container">
          <h1>Hello World</h1>
          <p>Countries I've visited: {countriesVisited[currentCountryIndex]}</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
