import React, { useState, useEffect } from 'react';
import '../styles/Home.css';

const helloTranslations = [
  { language: 'English', text: 'Hello World' },
  { language: 'Portuguese', text: 'Olá Mundo' },
  { language: 'Spanish', text: 'Hola Mundo' },
  { language: 'French', text: 'Bonjour le Monde' },
  { language: 'Italian', text: 'Ciao Mondo' },
];

function Home() {
  const [currentHelloIndex, setCurrentHelloIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentHelloIndex((prevIndex) => (prevIndex + 1) % helloTranslations.length);
    }, 2000);

    return () => clearInterval(interval); // Limpar o intervalo quando o componente for desmontado
  }, []);

  return (
    <div
      style={{
        position: 'relative', // Alterado de 'absolute' para 'relative' para evitar sobreposição com o conteúdo
        minHeight: '100vh', // Garantir que o conteúdo ocupe toda a altura da tela
        backgroundImage: 'url(/images/bg-image.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        zIndex: 0, // Garantir que o fundo não sobreponha o conteúdo
      }}
    >
      <div className="home-container">
        <div className="image-container">
          <img src="/images/my-photo.jpg" alt="Luis Oliveira" />
        </div>
        <div className="text-container">
          <h1>{helloTranslations[currentHelloIndex].text}</h1>
          <p className="animate-pulse text-xl text-white shadow-lg">A dev on a quest: to learn, to build, to conquer.</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
